import { createSelector } from 'reselect';

const COLLECTION_ID_MAP = {
	hats: 1,
	shoes: 2,
	jackets: 3,
	womens: 4,
	mens: 5,
};

const selectShop = state => state.shop;

export const selectCollections = createSelector([selectShop], shop => {
	return shop.collections;
});

//implementing curried function--> function returning another function
export const selectCollection = collectionUrlParam => {
	return createSelector([selectCollections], collections => {
		return collections.find(collection => {
			return collection.id === COLLECTION_ID_MAP[collectionUrlParam];
		});
	});
};
